    ENDIF

    EXITS("FIELD_DEPENDENT_TYPE_SET_AND_LOCK")
    RETURN
999 ERRORSEXITS("FIELD_DEPENDENT_TYPE_SET_AND_LOCK",ERR,ERROR)
