            SELECT CASE(componentType)
            CASE(FIELD_ALL_COMPONENTS_TYPE)
              startComponentIdx=1
              endComponentIdx=INTERPOLATION_PARAMETERS%FIELD_VARIABLE%NUMBER_OF_COMPONENTS
            CASE(FIELD_GEOMETRIC_COMPONENTS_TYPE)
