        ENDIF
      ELSE
        LOCAL_ERROR="Field number "//TRIM(NumberToVString(FIELD%USER_NUMBER,"*",ERR,ERROR))// &
          & " has not been finished."
        CALL FlagError(LOCAL_ERROR,ERR,ERROR,*999)
      ENDIF
