    TYPE(VARYING_STRING) :: LOCAL_ERROR

    ENTERS("FIELD_PARAMETER_SET_GET_ELEMENT_INTG",ERR,ERROR,*999)

    IF(ASSOCIATED(FIELD)) THEN
