                    CASE(FIELD_ELEMENT_AND_EXT_LINE_BASED_INTERPOLATION)
                      !!!!!
                      CALL FlagError("FIELD_ELEMENT_AND_EXT_LINE_BASED_INTERPOLATION is not yet implemented here",ERR,ERROR,*999)
                      !!!!!
                    CASE(FIELD_NODE_BASED_INTERPOLATION)
