                ELSE
                  LOCAL_ERROR="The field parameter set type of "//TRIM(NumberToVString(FIELD_SET_TYPE,"*",ERR,ERROR))// &
                    & " has not been created on field number "//TRIM(NumberToVString(FIELD%USER_NUMBER,"*",ERR,ERROR))
