                                & TRIM(NumberToVString(DOMAIN_NODES%NODES(DOMAIN_LOCAL_NODE_NUMBER)% &
                                & NUMBER_OF_DERIVATIVES,"*",ERR,ERROR))//" derivatives."
                              CALL FlagError(LOCAL_ERROR,ERR,ERROR,*999)
                            ENDIF
                          ELSE
