            IF(FIELD_VARIABLE%DATA_TYPE==FIELD_INTG_TYPE) THEN
              IF(FIELD_SET_TYPE>0.AND.FIELD_SET_TYPE<=FIELD_NUMBER_OF_SET_TYPES) THEN
