        CALL FlagError(LOCAL_ERROR,ERR,ERROR,*999)
      ENDIF
    ELSE
      CALL FlagError("Field is not associated.",ERR,ERROR,*999)
    ENDIF

    EXITS("FIELD_TYPE_SET_AND_LOCK")
    RETURN
999 ERRORSEXITS("FIELD_TYPE_SET_AND_LOCK",ERR,ERROR)
    RETURN 1
