    !Local Variables
    INTEGER :: C_LENGTH,VS_LENGTH
    TYPE(FIELD_VARIABLE_TYPE), POINTER :: FIELD_VARIABLE
    TYPE(VARYING_STRING) :: LOCAL_ERROR

    ENTERS("FIELD_VARIABLE_LABEL_GET_C",ERR,ERROR,*999)

    IF(ASSOCIATED(FIELD)) THEN
